import React from "react";

import Movie from "./Movie";
import classes from "./Movies.module.css";
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import 'react-bootstrap-table2-paginator/dist/react-bootstrap-table2-paginator.min.css';

const Movies = ({ list }) => {
  let cards = <h3>Loading...</h3>;
  const columns = [{
	  dataField: 'DESCRIPTION',
	  text: 'Product ID'
	}, {
	  dataField: 'LIST_PRICE',
	  text: 'Product Name'
	}, {
	  dataField: 'ITEM_NUMBER',
	  text: 'Product Price'
	}];
  if (list) {
   // cards = list.map((m, i) => <Movie key={i} item={m} />);
	  
    cards = <BootstrapTable keyField='ITEM_NUMBER' data={ list } columns={ columns } pagination={ paginationFactory() } />;
  }
  
  return (
    <div className={classes.Container}>
      <div className={classes.ContainerInner}>{cards}</div>
    </div>
  );
};


export default Movies;
